//Teacher.java//A Subclass of the DiningHall Project//K. Meyerpublic class Teacher{	String lname;	boolean gen;	int seats, tableNum;		// constructors	public Teacher()		{					gen 	 = true;		lname 	 = "";		seats	 = 0 ;		tableNum = 0 ;		}		public Teacher(boolean G, String n, int s, int t)		{		gen    	 = G;							lname    = n;		seats	 = s ;		tableNum = t ;		}		//copy constructor by Andrew P.	public Teacher(Teacher oldTeacher)	{		gen = oldTeacher.getGender();		lname = oldTeacher.getName();		seats = oldTeacher.getSeats();		tableNum = oldTeacher.getTableNum();	}		//accessors	public int getTableNum()	{		return tableNum;	}		public String getName()	{		return lname;	}		public int getSeats()	{		return seats;	}		public boolean getGender()	{		return gen;	}	//modifiers	public void setTableNum(int tNum)	{		tableNum = tNum;	}		public void setName(String lName)	{		lname = lName;	}		public void setSeats(int Seats)	{		seats = Seats;	}		public void setGender(boolean G)	{		gen = G;	}}